import { categoryConstants } from "../actions/constants"

const initialState = {
    categories: [],
    loading: false,
    error: null
}

const buildNewCategories = (id, categories, category) => {
    let myCategories = [];
    for (let cat of categories) {
        if (cat._id == id) {
            myCategories.push({
                ...cat,
                children: cat.children && cat.children.length > 0 ? buildNewCategories(id, [...cat.children, {
                    _id: category._id,
                    name: category.name,
                    slug: category.slug,
                    parentId: category.parentId,
                    children: category.children
                }], category) : []
            })
        }
    }
    return myCategories
}

const categoryReducer = (state = initialState, action) => {
    switch (action.type) {
        case categoryConstants.GET_ALL_CATEGORIES_REQUEST:
            return {
                ...state,
                loading: true
            }
        case categoryConstants.GET_ALL_CATEGORIES_SUCCESS:
            return {
                ...state,
                loading: false,
                categories: action.payload.categories
            }
        case categoryConstants.GET_ALL_CATEGORIES_FAILED:
            return {
                ...state,
                loading: false,
                error: action.payload.error
            }
        case categoryConstants.ADD_NEW_CATEGORY_REQUEST:
            return {
                ...state,
                loading: true
            }
        case categoryConstants.ADD_NEW_CATEGORY_SUCCESS:
            const updatedCategories = buildNewCategories(action.payload.category.parentId, state.categories, action.payload.category)
            return {
                ...state,
                loading: false,
                categories: updatedCategories
            }
        case categoryConstants.ADD_NEW_CATEGORY_FAILURE:
            return {
                ...state,
                loading: false
            }
        default: return state
    }

}

export default categoryReducer
